let UPDATE_INPUT_COMMENT='UPDATE_INPUT_COMMENT';
let OPEN

let initialState={
    listTK:[
        {id:'1', img:'https://kenguruexpress.ru/images/services/dpd.png',to:'/footer/transportCompany/dpd'},
        {id:'2', img:'https://kenguruexpress.ru/images/services/cdek.png',to:'/footer/transportCompany/cdek'},
        {id:'3',img:'https://kenguruexpress.ru/images/services/pecom.jpg',to:'/footer/transportCompany/pecom'},
        {id:'4',img:'https://kenguruexpress.ru/images/services/energy.jpg',to:'/footer/transportCompany/energy'},
        {id:'5',img:'https://kenguruexpress.ru/images/services/jde.png',to:'/footer/transportCompany/jde'}
    ],
    infoTK:[
        {
            id:'1',
            name:'dpd',
            logo:'https://kenguruexpress.ru/images/services/dpd.png',
            to:'/footer/transportCompany/dpd',
            description:'DPD в России является признанным лидером российского рынка экспресс-доставки посылок и грузов и предлагает сервис на уровне современных мировых стандартов. DPD в России оказывает тысячам клиентам полный комплекс транспортно-логистических услуг и доставляет по всей территории России.',
            rating:'5',
            age:'22',
        }
    ],
    comment:[
        {
            id:'1',
            sender:'Максим В.',
            rating:'2',
            dateAndTime: '28.01.2019 18:47',
            text:'Говно, которое разъебало мне посылку.'
        },
        {
            id:'2',
            sender:'Кирилл С.',
            rating:'5',
            dateAndTime: '28.01.2019 19:47',
            text:'Все четко.'
        }
    ],
    newComment:'',
    nameUser:'',
    idUser:''
};

const TransportCompanyReducer=( state=initialState, action)=>
{
    switch (action.type) {
        case UPDATE_INPUT_COMMENT:
            return{
                ...state,
                newComment:action.bodyComment
            }
        default:
            return{...state}
    }
};

export const updateInputCommnent=(textComment)=>({type:UPDATE_INPUT_COMMENT, bodyComment:textComment});

export default TransportCompanyReducer;
