import React from 'react';
import classes from "./message.module.css";


const Message =(props)=>
{
   let postElement = React.createRef();
   let addSms = () =>
   {
        let text = postElement.current.value;
   };
    let postSms= () =>
    {
        let text = postElement.current.value;

    };
    return(
        <div className={classes.sms}>
            <div className={classes.iconsUser}>
                {props.icons}
            </div>
            <div className={classes.poleMessage}>
                <textarea ref={postElement} onChange={postSms}></textarea>
                <button onClick={addSms}>ddddd</button>
                <p>{props.message}</p>
            </div>
        </div>
    );
}
export default Message;
