import React from 'react';
import {connect} from "react-redux";
import { follow, getUsersThunkCreater, unFollow} from "../../redux/users-reducer";
import Users from "./users";
import Preloader from "../common/preloader/preloader";
import {Redirect} from "react-router-dom";

class UsersAPIComponent extends React.Component {

    componentDidMount() {
        this.props.getUsersThunkCreater(this.props.currentPage,this.props.pageSize);
    }
    onPageChanged = (pageNumber) => {
        this.props.getUsersThunkCreater(pageNumber,this.props.pageSize);
    };

    render() {
        return <>
            {
                this.props.isFetching ? <Preloader /> : null
            }
            <Users
                {...this.props}
                totalUsersCount={this.props.totalUsersCount} pageSize={this.props.pageSize} onPageChanged={this.onPageChanged}
                users={this.props.users} follow={this.props.follow}
                unFollow={this.props.unFollow} followingInProgress={this.props.followingInProgress}/></>
    }

}

let AuthRedirectComponent=(props)=>{
    if(!props.isAuth){return <Redirect to={"/login"}/>}
    return <UsersAPIComponent {...props}/>
}

let mapStateToProps=(state)=>{
    return{
        users:state.UsersPage.users,
        pageSize: state.UsersPage.pageSize,
        totalUsersCount:state.UsersPage.totalUsersCount,
        currentPage:state.UsersPage.currentPage,
        isFetching:state.UsersPage.isFetching,
        followingInProgress:state.UsersPage.followingInProgress,
        isAuth:state.Auth.isAuth

    }

};
 export default connect(mapStateToProps, { getUsersThunkCreater,  follow, unFollow})(AuthRedirectComponent);
