import React from 'react';
import {addPostActionCreator, updateNewPostActionCreator} from "../../../redux/content-reducer";
import AddPost from "./addPost";
 const AddPostContainer = (props)=>{
     let newsPost = React.createRef();
     let AddSms=()=>
     {
         props.dispatch(addPostActionCreator());
     }
     let PostChange = (sms)=>
     {
         let action = updateNewPostActionCreator(sms);
         props.dispatch(action);
     }
     return(<AddPost updateNewPostText={PostChange} addPost={AddSms} store={props.store.state}/>

     );
 }
 export default AddPostContainer;
