import * as axios from "axios";

const instance = axios.create({
    baseURL:'https://reqres.in/api/',
    headers:{"Access-Control-Allow-Origin": "*",
        }
});

export const userAPI={
    getUsers(currentPage,pageSize){
        return instance.get(`users?page=${currentPage}&count=${pageSize}`).then(response=>{
            return response.data;
        })},
    getMe(){
        return instance.get(`auth/me`).then(response=>{
            return response.data;
        })},
    deleteFollowed(id){
        return  instance.delete(`follow/${id}`).then(response=>{
            return response.data;
        })},
    postFollowed(id){
        return  instance.post(`follow/${id}`).then(response=>{
            return response.data;
        })},
    getProfileData(id){
        return instance.get(`profile/` + id)
    }
};
export const ProfileAPI = {
    putStatus(status){
        return instance.post('profile/status', {status:status})
    },
    getStatus(userID){
        return  instance.get(`profile/status/`+ userID)
    }

};
export const RegistrationAPI = {

    putRegistration(surname,name,middleName,phone,email,password){
        console.log(surname,name,middleName,phone,email,password);
        let user={
            "email": "eve.holt@reqres.in",
            "password": "pistol"
        };
        return instance.post('register', {user}).then()
    }
};
