import React from 'react';
import Profile from "./profile";
import {connect} from "react-redux";
import {addProfileData, getStatus, updateStatus} from "../../../redux/content-reducer";
import {withRouter} from "react-router-dom";

class ProfileContainer extends React.Component{

    componentDidMount() {
        let userID = this.props.match.params.userId;
       this.props.addProfileData(userID);
       this.props.getStatus(userID);
    }
    render() {
        return <Profile {...this.props} profile={this.props.profile} status={this.props.status} updateStatus={this.props.updateStatus} />
    }
}
let mapStateToProps =(state)=>({

        profile:state.profilePage.profile,
        status:state.profilePage.status
});
let WithUrlDataContainerComponent = withRouter(ProfileContainer);
export default connect(mapStateToProps,{addProfileData,getStatus,updateStatus})(WithUrlDataContainerComponent);
