import React from 'react';

class ProfileStatus extends React.Component {
    statusInputRef = React.createRef();

    state = {
        editMode: false
    };

    activateEditMode=()=>{
        this.setState({
            editMode:true
        })
    };
    deActivateEditMode=()=>{
        this.setState({
            editMode:false

        });
        this.props.putStatus(this.statusInputRef.current.value);
    };
    render() {
        return (
            <div>
                {!this.state.editMode && <div><span onDoubleClick={this.activateEditMode}>{this.props.status}</span></div>
                }
                {this.state.editMode &&  <div><input ref={this.props.statusInputRef} autoFocus={true} onBlur={this.deActivateEditMode} value = { this.props.status } /></div>
                }
            </div>

        )
    }
}
export default ProfileStatus;
