import classes from "../components/header/header.module.css";
import {rerenderEntireTree} from "../render";

let state =
    {
        friends: {
            friend: [
                {id: 1, name: 'Marina'},
                {id: 2, name: 'Sveta'},
                {id: 3, name: 'Pasha'}
            ]
        },
        navigations: {
            navigation: [
                {id: 'profile', name: 'Profile', active: classes.active},
                {id: 'dialog', name: 'Dialog', active: classes.active},
                {id: 'news', name: 'News', active: classes.active},
                {id: 'music', name: 'Music', active: classes.active},
                {id: 'setting', name: 'Setting', active: classes.active},
            ]
        },
        profilePage: {
            postData: [
                {id: 1, message: 'Hi Lerill', like: 10},
                {id: 1, message: 'sdlfkkhfshlkj', like: 5}
            ],
            newPostText:'dfdffd'},
        messagePage: {
            dialogsData: [
                {id: 1, name: 'Ivan'},
                {id: 2, name: 'Dima'},
                {id: 3, name: 'Gromik'}
            ],
            newMessagePost:'grom',
            messagesData: [
                {id: 1, message: 'Hello, Ivan!!'},
                {id: 2, message: 'My name Kirill!!!'},
                {id: 3, message: 'Idi naxyi'}
            ]
        }
    };
   export let  addPost = () =>
    {
        let newPost =
            {
                id:5,
                message:state.profilePage.newPostText,
                like:5
            };
        state.profilePage.postData.push(newPost);
        state.profilePage.newPostText='';
        rerenderEntireTree(state);
    }
export let  updateNewPostText = (newText) =>
{
    state.profilePage.newPostText = newText;
    rerenderEntireTree(state);
}
export let addSms=()=>
{
    let newSms=
        {
            id:4, 
            message:state.messagePage.newMessagePost
        }
}
export default state;
