import React from 'react';
import classes from './addPost.module.css';
 const AddPost = (props)=>{
     let newPostElement = React.createRef();
     return(
         <div className={classes.blockFast}>
             <div className={classes.item}><p>My posts</p></div>
             <div className={classes.textPole}>
                 <textarea onChange={props.updateMessage} ref={newPostElement} value={props.profilePage.newPostText}></textarea>
             </div>
             <div className={classes.buttonAdd}>
                 <button onClick={props.addMessage}>Send</button>
             </div>
         </div>
     );
 };
 export default AddPost;
