const ADD_POST = 'ADD-POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';
const ContentReducer=(state, action)=>
{
    let initialState = {
            postData: [
                {id: 1, message: 'Hi Lerill', like: 10},
                {id: 1, message: 'sdlfkkhfshlkj', like: 5}
            ],
            newPostText:'511'},
            messagePage: {
            dialogsData: [
                {id: 1, name: 'Ivan'},
                {id: 2, name: 'Dima'},
                {id: 3, name: 'Gromik'}
                ],
            newMessagePost:'grom',
            messagesData: [
                {id: 1, message: 'Hello, Ivan!!'},
                {id: 2, message: 'My name Kirill!!!'},
                {id: 3, message: 'Idi naxyi'}
                ]
    }
debugger;
    switch (action.type) {
        case ADD_POST:
            let newPost =
                {
                    id:5,
                    message:state.newPostText,
                    like:5
                };
            state.postData.push(newPost);
            state.newPostText='';
            return state;
        case UPDATE_NEW_POST_TEXT:
            state.newPostText = action.newText;
            return state;
        default:
            return state;
    }
};
export const addPostActionCreator = () =>
{
    return{type:ADD_POST}
};
export const updateNewPostActionCreator=(sms)=>
{
    return{type:UPDATE_NEW_POST_TEXT ,newText:sms}
};
export default ContentReducer;
