import React from 'react';
import classes from './addPost.module.css';
 const AddPost = (props)=>{
     console.log(props);
     let newsPost = React.createRef();
     let react=()=>
     {
         let sms = newsPost.current.value;
         props.addPost(sms);
         sms.curent.value ='';
     }
     let onPostChange = ()=>
     {
         let sms = newsPost.current.value;
         props.updateNewPostText(sms);
     }
     return(
         <div className={classes.blockFast}>
             <div className={classes.item}><p>My posts</p></div>
             <div className={classes.textPole}>
                 <textarea onChange={onPostChange} ref={newsPost} value={props.posts.newPostText}></textarea>
             </div>
             <div className={classes.buttonAdd}>
                 <button onClick={react}>Send</button>
             </div>
         </div>
     );
 }
 export default AddPost;
