import React from 'react';
import classes from './addPost.module.css';
 const AddPost = (props)=>{
     let newsPost = React.createRef();
     let onAddSms=()=>
     {
         props.addSms();
     }
     let onPostChange = ()=>
     {
         let sms = newsPost.current.value;
         props.PostChange(sms);
     }
     debugger;
     return(
         <div className={classes.blockFast}>
             <div className={classes.item}><p>My posts</p></div>
             <div className={classes.textPole}>
                 <textarea onChange={onPostChange} ref={newsPost} value={props.store.state.profilePage.newPostText}></textarea>
             </div>
             <div className={classes.buttonAdd}>
                 <button onClick={onAddSms}>Send</button>
             </div>
         </div>
     );
 }
 export default AddPost;
