import React from 'react';
import './index.module.css';
import * as serviceWorker from './serviceWorker';
import state, {addPost, addSms, subscribe, updateNewMessagText, updateNewPostText} from './redux/state';
import ReactDOM from "react-dom";
import App from "./App";

 let rerenderEntireTree=()=>
{
    ReactDOM.render(<App state = {state} addSms={addSms} addPost={addPost} updateNewMessagText={updateNewMessagText} updateNewPostText={updateNewPostText}/>,
        document.getElementById('root'));
}
rerenderEntireTree(state);
 subscribe(rerenderEntireTree());
// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
