import React from 'react';
import {connect} from "react-redux";
import {
    acceptFollow, acceptUnFollow,
    getUsersThunkCreater,
    setInFollowing,
} from "../../redux/users-reducer";
import * as axios from "axios";
import Users from "./users";
import Preloader from "../common/preloader/preloader";
import {getUsers} from "../../API/api";
class UsersAPIComponent extends React.Component {
    componentDidMount() {

        this.props.getUsersThunkCreater(this.props.currentPage,this.props.pageSize);

    }

    onPageChanged = (pageNumber) => {
        this.props.getUsersThunkCreater(pageNumber,this.props.pageSize);
    };


    render() {
        return <>
            {
                this.props.isFetching ? <Preloader /> : null
            }
            <Users totalUsersCount={this.props.totalUsersCount} pageSize={this.props.pageSize}
                      currentPage={this.props.currentPage} onPageChanged={this.onPageChanged} users={this.props.users}
                      follow={this.props.follow} unfollow={this.props.unFollow} setInFollowing={this.props.setInFollowing} followingInProgress={this.props.followingInProgress}/></>
    }
}
let mapStateToProps=(state)=>{
    return{
        users:state.UsersPage.users,
        pageSize: state.UsersPage.pageSize,
        totalUsersCount:state.UsersPage.totalUsersCount,
        currentPage:state.UsersPage.currentPage,
        isFetching:state.UsersPage.isFetching,
        followingInProgress:state.UsersPage.followingInProgress
    }
};
 export default connect(mapStateToProps, { getUsersThunkCreater, acceptFollow, acceptUnFollow, setInFollowing})(UsersAPIComponent);
