import classes from "../components/header/header.module.css";

const ADD_SMS = 'ADD-SMS';
const UPDATE_NEW_SMS_TEXT = 'UPDATE-NEW-SMS-TEXT';
let initialState={
    dialogsData: [
        {id: 1, name: 'Ivan'},
        {id: 2, name: 'Dima'},
        {id: 3, name: 'Gromik'}
    ],
    newMessagePost:'grom',
    messagesData: [
        {id: 1, message: 'Hello, Ivan!!'},
        {id: 2, message: 'My name Kirill!!!'},
        {id: 3, message: 'Idi naxyi'}
    ]
};
const DialogsReducer=(state=initialState,action)=>
{

    switch (action.type) {
        case ADD_SMS:
            let newSms=
                {
                    id:4,
                    message: state.newMessagePost
                };
            let stateCopy = [...state];
            stateCopy.post = {}
            state.messagesData.push(newSms);
            state.newMessagePost='';
            return state;
        case UPDATE_NEW_SMS_TEXT:
            state.newMessagePost = action.newText;
            return state;
        default:
            return state;
    }
}
export const addSmsActionCreator = () =>
{
    return{type:ADD_SMS}
}
export const updateNewSmsActionCreator=(text)=>
{
    return{type:UPDATE_NEW_SMS_TEXT,newText:text}
}
export default DialogsReducer;
