import React from 'react';
import classes from './dialogs.module.css';
import Dialog from "./dialog/dialog";
import Chat from "./chat/chat";
const Dialogs = (props) => {
    let dialogsElements = props.messagePage.dialogsData.map(dialog => <Dialog name={dialog.name} id={dialog.id}/>);
    let dialogsMessage = props.messagePage.messagesData.map(message => <Chat sms={message.message}/>);
    let newMessagElement = React.createRef();
    return (
        <div className={classes.content}>
            <div className={classes.dialogs}>
                {dialogsElements}
            </div>
            <div className={classes.pole}>
                <textarea onChange={props.updateMessage}  value={props.messagePage.newMessagePost}></textarea><br/>
                <button onClick={props.addMessage}>аавыав</button>
                {dialogsMessage}
            </div>
        </div>
    );
}
export default Dialogs;
