import classes from "../components/header/header.module.css";
import ContentReducer from "./content-reducer";
import DialogsReducer from "./dialogs-reducer";

 let rerenderEntireTree=()=>
 {
     console.log('ffffff');
 }
let store=
    {
        _state: {
            saidbar:{
                friends: {
                    friend: [
                        {id: 1, name: 'Marina'},
                        {id: 2, name: 'Sveta'},
                        {id: 3, name: 'Pasha'}
                    ]
                },
                navigations: {
                    navigation: [
                        {id: 'profile', name: 'Profile', active: classes.active},
                        {id: 'dialog', name: 'Dialog', active: classes.active},
                        {id: 'news', name: 'News', active: classes.active},
                        {id: 'music', name: 'Music', active: classes.active},
                        {id: 'setting', name: 'Setting', active: classes.active},
                    ]
                }
                },
                profilePage: {
                    postData: [
                        {id: 1, message: 'Hi Lerill', like: 10},
                        {id: 1, message: 'sdlfkkhfshlkj', like: 5}
                    ],
                    newPostText:'511'},
                messagePage: {
                    dialogsData: [
                        {id: 1, name: 'Ivan'},
                        {id: 2, name: 'Dima'},
                        {id: 3, name: 'Gromik'}
                    ],
                    newMessagePost:'grom',
                    messagesData: [
                        {id: 1, message: 'Hello, Ivan!!'},
                        {id: 2, message: 'My name Kirill!!!'},
                        {id: 3, message: 'Idi naxyi'}
                    ]
                }
            },
        getState(){
            return this._state;
        },
        rerenderEntireTree() {
            console.log('ffffff');
        },
        subscribe(observer) {
           this.rerenderEntireTree = observer;
        },
        dispatch(action) {

            this._state.profilePage = ContentReducer(this._state.profilePage, action);
            this._state.messagePage = DialogsReducer( this._state.messagePage, action);
            this._state.saidbar = SaidbarReducer( this._state.saidbar, action);
            this.rerenderEntireTree(this._state);

        }
    };



export default store;
window.store=store;
