import React from 'react';
import {addSmsActionCreator, updateNewSmsActionCreator} from "../../redux/dialogs-reducer";
import Dialogs from "./dialogs";
import StoreContext from "../../StoreContext";

const DialogsContainer = () => {


    let addMessage = () => {
        store.dispatch(addSmsActionCreator());
    }
    let updateMessage = (text) => {
        let action = updateNewSmsActionCreator(text);
        store.dispatch(action);
    };
    debugger;
    return (<StoreContext.Consumer>
            {(store) => {
                let state =  store.getState();
                return <Dialogs updateMessage={updateMessage} addMessage={addMessage}
                                store={store.state.messagePage}/>
            }
            }
        </StoreContext.Consumer>
    );

}
export default DialogsContainer;
