import React from 'react';
import classes from './dialogs.module.css';
import Dialog from "./dialog/dialog";
import Chat from "./chat/chat";
const Dialogs = (props) => {
    let dialogsElements = props.elements.map(dialog => <Dialog name={dialog.name} id={dialog.id}/>);
    let dialogsMessage = props.message.map(message => <Chat sms={message.message}/>);
    let newMessagElement = React.createRef();
    let addSmsActionCreator = () =>
    {
        return{type:'ADD-SMS'}
    }
    let updateNewSmsActionCreator=(text)=>
    {
        return{type:'UPDATE-NEW-SMS-TEXT',newText:text}
    }
    let addMessag=()=>
    {

        props.dispatch(addSmsActionCreator());
    }
    let updateMessage = () =>
    {
        let text = newMessagElement.current.value;
        let action = updateNewSmsActionCreator(text);
        props.dispatch(action);
    };
    return (
        <div className={classes.content}>
            <div className={classes.dialogs}>
                {dialogsElements}
            </div>
            <div className={classes.pole}>
                <textarea onChange={updateMessage} ref={newMessagElement} value={props.appMessage.newMessagePost}></textarea><br/>
                <button onClick={addMessag}>аавыав</button>
                {dialogsMessage}
            </div>
        </div>
    );
}
export default Dialogs;
