const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';
let initialState = {
    users: [
        {id: 1, photoURL:'https://www.outstandingcolleges.com/wp-content/uploads/2015/03/Michael-Schoendorf.jpg', followed: false, fullName: 'Ivan Butov', status: 'i very big xuiska!!', location:{city:'Minsk',country:'Belarus'}},
        {id: 2, photoURL:'https://www.outstandingcolleges.com/wp-content/uploads/2015/03/Michael-Schoendorf.jpg', followed: true, fullName: 'Kirill Sankov', status: 'i very big piska!!', location:{city:'Barnaul',country:'Russia'}},
        {id: 3, photoURL:'https://www.outstandingcolleges.com/wp-content/uploads/2015/03/Michael-Schoendorf.jpg', followed: true, fullName: 'Maksim Velik', status: 'i very big vaginla!!', location:{city:'Kiev',country:'Ykraina'}},
        {id: 4, photoURL:'https://www.outstandingcolleges.com/wp-content/uploads/2015/03/Michael-Schoendorf.jpg', followed: false, fullName: 'Atatoliy Fedorov', status: 'i very big boss!!', location:{city:'Moscow',country:'Russia'}}
    ],
    newPostText:'511'};
const UsersReducer=( state=initialState, action)=>
{
    switch (action.type) {
        case FOLLOW:
            return {...state,
            users: state.users.map(u=>{
                if (u.id===action.userID){
                    return {...u, followed:true};
                }

            })};

        case UNFOLLOW:
            return {...state,
                users: state.users.map(u=>{
                    if (u.id=== action.ID){
                        return {...u, followed:false};
                    }

                })};
        case SET_USERS:
            return {
                ...state, users:[...state.users, ...action.users]
            }
        default:
            return state;
    }
};
export const followAC = (userID) => ({type:FOLLOW, userID});
export const unFollowAC=(ID)=> ({type:UNFOLLOW,ID });
export const setUsersAC=(users)=> ({type:SET_USERS,users });

export default UsersReducer;
