import React from 'react';
import classes from './addPost.module.css';
 const AddPost = (props)=>{
     let onAddPost=()=>
     {
         props.addPostActionCreator();
     }
     let onUpdatePost=(e)=>{
         props.updateNewPostActionCreator(e.target.value);
     }
     return(
         <div className={classes.blockFast}>
             <div className={classes.item}><p>My posts</p></div>
             <div className={classes.textPole}>
                 <textarea onChange={onUpdatePost} value={props.profilePage.newPostText}></textarea>
             </div>
             <div className={classes.buttonAdd}>
                 <button onClick={onAddPost}>Send</button>
             </div>
         </div>
     );
 };
 export default AddPost;
