import React from 'react';
import {addPostActionCreator, updateNewPostActionCreator} from "../../../redux/content-reducer";
import AddPost from "./addPost";
import StoreContext from "../../../StoreContext";
import {addSmsActionCreator, updateNewSmsActionCreator} from "../../../redux/dialogs-reducer";
import {connect} from "react-redux";
import Dialogs from "../../dialogs/dialogs";
 const AddPostContainer = ()=>{

     return(<StoreContext.Consumer>
         {

             (store) => {
                 let AddSms=()=>
                 {
                     store.dispatch(addPostActionCreator());
                 }
                 let PostChange = (sms)=>
                 {
                     let action = updateNewPostActionCreator(sms);
                     store.dispatch(action);
                 }
                 return <AddPost updateNewPostSms={PostChange} AddSms={AddSms} store={store}/>
             }
         }</StoreContext.Consumer>
         );

 }
let mapStateToProps = (state) => {
    return {
        store: state.messagePage
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        updateMessage:(text)=>{
            dispatch(updateNewSmsActionCreator(text));
        },
        addMessage:()=>{
            dispatch(addSmsActionCreator());
        }
    }
}
const DialogsContainer = connect(mapStateToProps, mapDispatchToProps)(Dialogs);
 export default AddPostContainer;
