import React from 'react';
import './index.module.css';
import * as serviceWorker from './serviceWorker';
import store from './redux/state';
import ReactDOM from "react-dom";
import App from "./App";

 let rerenderEntireTree=(state)=>
{
    ReactDOM.render(<App state = {state} addSms={store.addSms.bind(store)} addPost={store.addPost.bind(store)} updateNewMessagText={store.updateNewMessagText.bind(store)} updateNewPostText={store.updateNewPostText.bind(store)}/>,
        document.getElementById('root'));
}
rerenderEntireTree(store.getState());
 store.subscribe(rerenderEntireTree);
// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
