import React from 'react';
import {addPostActionCreator, updateNewPostActionCreator} from "../../../redux/content-reducer";
import AddPost from "./addPost";
import StoreContext from "../../../StoreContext";
 const AddPostContainer = (props)=>{
     let AddSms=()=>
     {
         props.store.store.dispatch(addPostActionCreator());
     }
     let PostChange = (sms)=>
     {
         let action = updateNewPostActionCreator(sms);
         props.store.store.dispatch(action);
     }
     return(<StoreContext.Consumer>
         {
             (store) => {
                 <AddPost updateNewPostSms={PostChange} AddSms={AddSms} store={props.store}/>
             }
         }</StoreContext.Consumer>
         );
 }
 export default AddPostContainer;
