import React from 'react';
import classes from './users.module.css';
import {NavLink} from "react-router-dom";
import * as axios from "axios";

let Users = (props) => {
    let pagesCount = props.totalUsersCount / props.pageSize;
    let pages = [];
    for (let i = 1; i <= Math.ceil(pagesCount); i++) {
        pages.push(i);
    }
    return <div>
        {pages.map(p => {
            return <span onClick={() => {
                props.onPageChanged(p)
            }} className={props.currentPage === p && classes.selectedPage}>{p}</span>
        })}
        {
            props.users.map(u => <div key={u.id}>
                <div className={classes.row}>
                    <div className={classes.column}>
                        <div className={classes.img}>
                            <NavLink to={'/profile/' + u.id}>
                            <img src={u.photos.small}/>
                            </NavLink>
                        </div>
                    </div>
                    <div className={classes.column}>
                        <div>
                            <p>{u.name}</p>
                            <p>{u.status}</p>
                        </div>
                        <div className={classes.location}>
                            {/*<p>{u.location.city}</p>*/}
                            {/*<p>{u.location.country}</p>*/}
                        </div>

                    </div>
                </div>
                <div className={classes.row}>
                    {u.followed == true
                        ? <button onClick={() =>
                        {
                            // axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`,{ withCredentials: true, headers:{"API-KEY":"e20aac34-0ed5-4a7e-872e-3bec534ebf35"}}).then(response => {
                            //    if(response.data.resultCode == 0){
                            //        props.unfollow(u.id)
                            //    }
                            // });
                            props.unfollow(u.id)
                        }}>UnFollow</button>
                        :<button onClick={() =>
                        {
                            // axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`,{},{ withCredentials: true, headers:{"API-KEY":"e20aac34-0ed5-4a7e-872e-3bec534ebf35"}}).then(response => {
                            //     if(response.data.resultCode == 0){
                            //         debugger
                            //         props.follow(u.id)
                            //     }
                            // });
                            props.follow(u.id)
                        }}>UnFollow</button>}
                </div>
            </div>)
        }
    </div>
}
export default Users;
