import React from 'react';
import classes from './dialogs.module.css';
import Dialog from "./dialog/dialog";
import Chat from "./chat/chat";
import {addSmsActionCreator, updateNewSmsActionCreator} from "../../redux/dialogs-reducer";
const Dialogs = (props) => {
    let state= props.messagePage;
    let dialogsElements = state.dialogsData.map(dialog => <Dialog name={dialog.name} id={dialog.id}/>);
    let dialogsMessage = state.messagesData.map(message => <Chat sms={message.message}/>);
    let onAddMessage=()=>
    {
        props.addSmsActionCreator();
    }
    let onUpdateMessage=(e)=>{
        props.updateNewSmsActionCreator(e.target.value);
    }
    return (
        <div className={classes.content}>
            <div className={classes.dialogs}>
                {dialogsElements}
            </div>
            <div className={classes.pole}>
                <textarea onChange={onUpdateMessage}  value={state.newMessagePost}></textarea><br/>
                <button onClick={onAddMessage}>член</button>
                {dialogsMessage}
            </div>
        </div>
    );
}
export default Dialogs;
