import classes from "../components/header/header.module.css";
const ADD_POST = 'ADD-POST';
const ADD_SMS = 'ADD-SMS';
const UPDATE_NEW_SMS_TEXT = 'UPDATE-NEW-SMS-TEXT';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';
 let rerenderEntireTree=()=>
 {
     console.log('ffffff');
 }
let store=
    {
        _state: {
                friends: {
                    friend: [
                        {id: 1, name: 'Marina'},
                        {id: 2, name: 'Sveta'},
                        {id: 3, name: 'Pasha'}
                    ]
                },
                navigations: {
                    navigation: [
                        {id: 'profile', name: 'Profile', active: classes.active},
                        {id: 'dialog', name: 'Dialog', active: classes.active},
                        {id: 'news', name: 'News', active: classes.active},
                        {id: 'music', name: 'Music', active: classes.active},
                        {id: 'setting', name: 'Setting', active: classes.active},
                    ]
                },
                profilePage: {
                    postData: [
                        {id: 1, message: 'Hi Lerill', like: 10},
                        {id: 1, message: 'sdlfkkhfshlkj', like: 5}
                    ],
                    newPostText:'511'},
                messagePage: {
                    dialogsData: [
                        {id: 1, name: 'Ivan'},
                        {id: 2, name: 'Dima'},
                        {id: 3, name: 'Gromik'}
                    ],
                    newMessagePost:'grom',
                    messagesData: [
                        {id: 1, message: 'Hello, Ivan!!'},
                        {id: 2, message: 'My name Kirill!!!'},
                        {id: 3, message: 'Idi naxyi'}
                    ]
                }
            },
        getState(){
            return this._state;
        },
        rerenderEntireTree() {
            console.log('ffffff');
        },
        subscribe(observer) {
           this.rerenderEntireTree = observer;
        },
        dispatch(action) {

             if (action.type === ADD_POST)
             {
                 let newPost =
                     {
                         id:5,
                         message:this._state.profilePage.newPostText,
                         like:5
                     };
                 this._state.profilePage.postData.push(newPost);
                 this._state.profilePage.newPostText='';
                 this.rerenderEntireTree(this._state);
             }
             else if(action.type === UPDATE_NEW_POST_TEXT)
                 {
                     this._state.profilePage.newPostText = action.newText;
                     this.rerenderEntireTree(this._state);
                 }
             else if(action.type === ADD_SMS)
             {
                 let newSms=
                     {
                         id:4,
                         message: this._state.messagePage.newMessagePost
                     };
                 this._state.messagePage.messagesData.push(newSms);
                 this._state.messagePage.newMessagePost='';
                 this.rerenderEntireTree(this._state);
             }
             else if (action.type === UPDATE_NEW_SMS_TEXT)
             {
                 this._state.messagePage.newMessagePost = action.newText;
                 this.rerenderEntireTree(this._state);
             }
             else
                 {
                     alert('Введи верно action');
                 }
        }
    };
export const addSmsActionCreator = () =>
{
    return{type:ADD_SMS}
}
export const updateNewSmsActionCreator=(text)=>
{
    return{type:UPDATE_NEW_SMS_TEXT,newText:text}
}
export const addPostActionCreator = () =>
{
    return{type:ADD_POST}
}
export const updateNewPostActionCreator=(sms)=>
{
    return{type:UPDATE_NEW_POST_TEXT ,newText:sms}
}

export default store;
window.store=store;
